mapper.select:
select * from setmeal

    and category_id = #{categoryId}
    and name like concat('%',#{name},'%')
    and status = #{status}

order by create_time desc

mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, status = #{status}, description = #{description}, image = #{image},
where id = #{id}

mapper.select:
select * from setmeal

     and name like concat('%',#{name},'%') 
     and category_id = #{categoryId} 
     and status = #{status}
